import React, {useState} from 'react';

export default function ProductList({products, refresh}){
  const [form, setForm] = useState({name:'',description:'',price:'',stock:''});
  const submit = (e)=>{
    e.preventDefault();
    fetch('http://localhost:8080/api/products',{
      method:'POST', headers:{'Content-Type':'application/json'},
      body: JSON.stringify({name:form.name, description:form.description, price: parseFloat(form.price), stock: parseInt(form.stock||0)})
    }).then(()=>{ setForm({name:'',description:'',price:'',stock:''}); refresh(); });
  }
  return (
    <div>
      <form onSubmit={submit} style={{marginBottom:20}}>
        <input placeholder='Name' value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required />
        <input placeholder='Description' value={form.description} onChange={e=>setForm({...form,description:e.target.value})} />
        <input placeholder='Price' value={form.price} onChange={e=>setForm({...form,price:e.target.value})} required />
        <input placeholder='Stock' value={form.stock} onChange={e=>setForm({...form,stock:e.target.value})} />
        <button type='submit'>Add</button>
      </form>
      <ul>
        {products.map(p=>(
          <li key={p.id} style={{marginBottom:8}}>
            <strong>{p.name}</strong> — ₹{p.price} — stock: {p.stock}
            <div style={{marginTop:6}}>
              <button onClick={()=>{
                fetch('http://localhost:8080/api/cart',{
                  method:'POST', headers:{'Content-Type':'application/json'},
                  body: JSON.stringify({productId:p.id, productName:p.name, price:p.price, quantity:1})
                }).then(()=>alert('Added to cart'));
              }}>Add to cart</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  )
}
