import axios from "axios";

const API_BASE_URL = "http://localhost:8080/api/cart";

const CartService = {
  getCart: () => axios.get(API_BASE_URL),
  addToCart: (product) => axios.post(`${API_BASE_URL}/add`, product),
  clearCart: () => axios.delete(`${API_BASE_URL}/clear`)
};

export default CartService;
