import React, { useState } from "react";
import ProductList from "./components/ProductList";
import Cart from "./components/Cart";
import Checkout from "./components/Checkout";

function App() {
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  // Function to trigger cart refresh
  const refreshCart = () => setRefreshTrigger((prev) => prev + 1);

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
        <div className="container">
          <a className="navbar-brand fw-bold" href="/">üõçÔ∏è Local Seller</a>
          <div>
            <a className="btn btn-light me-2" href="#products">Products</a>
            <a className="btn btn-light me-2" href="#cart">Cart</a>
            <a className="btn btn-warning" href="#checkout">Checkout</a>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <section id="products" className="mb-5">
          <h2 className="text-center mb-4">Available Products</h2>
          <ProductList refreshCart={refreshCart} />
        </section>

        <section id="cart" className="mb-5">
          <h2 className="text-center mb-4">Your Cart</h2>
          <Cart refreshTrigger={refreshTrigger} />
        </section>

        <section id="checkout">
          <h2 className="text-center mb-4">Checkout</h2>
          <Checkout />
        </section>
      </div>
    </div>
  );
}

export default App;
