import React, {useEffect, useState} from 'react';
import ProductList from './components/ProductList';
import Cart from './components/Cart';

export default function App(){
  const [products, setProducts] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:8080/api/products').then(r=>r.json()).then(setProducts).catch(()=>setProducts([]));
  },[]);

  return (
    <div style={{fontFamily:'Arial', padding:20}}>
      <h1>Local Seller Demo</h1>
      <div style={{display:'flex', gap:40}}>
        <div style={{flex:1}}>
          <h2>Products</h2>
          <ProductList products={products} refresh={()=>{
            fetch('http://localhost:8080/api/products').then(r=>r.json()).then(setProducts)
          }} />
        </div>
        <div style={{width:360}}>
          <h2>Cart</h2>
          <Cart refreshProducts={()=>{
            fetch('http://localhost:8080/api/products').then(r=>r.json()).then(setProducts)
          }} />
        </div>
      </div>
    </div>
  )
}
