import React, { useState } from "react";
import ProductService from "../services/ProductService";

const ProductForm = ({ onProductAdded }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    ProductService.addProduct({ name, description, price, image })
      .then(() => {
        alert("Product added!");
        setName(""); setDescription(""); setPrice(""); setImage("");
        if(onProductAdded) onProductAdded(); // refresh products list
      })
      .catch(err => console.error(err));
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4">
      <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
      <input type="text" placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} required />
      <input type="number" step="0.01" placeholder="Price" value={price} onChange={e => setPrice(e.target.value)} required />
      <input type="text" placeholder="Image URL" value={image} onChange={e => setImage(e.target.value)} />
      <button type="submit" className="btn btn-success">Add Product</button>
    </form>
  );
};

export default ProductForm;
